#define _CRT_SECURE_NO_WARNINGS 1
#include "Stack.h"
#include <stdbool.h>

bool isValid(char * s)
{
	int len = strlen(s);
	int i = 0;
	bool ret = true;
	Stack ps;
	StackInit(&ps);
	for (i = 0; i < len; i++)
	{
		if (s[i] == '(' || s[i] == '[' || s[i] == '{')
		{
			StackPush(&ps, s[i]);
		}
		else
		{
			if (StackEmpty(&ps))
			{
				ret = false;
				break;
			}
			char tmp = StackTop(&ps);
			if (tmp == '(' && s[i] != ')')
			{
				ret = false;
				break;
			}
			else if (tmp == '{' && s[i] != '}')
			{
				ret = false;
				break;
			}
			else if (tmp == '[' &&s[i] != ']')
			{
				ret = false;
				break;
			}
			StackPop(&ps);
		}
	}
	if (StackEmpty(&ps) == 0)
	{
		ret = false;
	}
	StackDestroy(&ps);
	return ret;
}


